import React, { useState } from 'react';
import axios from 'axios';

const InvoiceForm = ({ fetchInvoices }) => {
  const [clientName, setClientName] = useState('');
  const [tax, setTax] = useState();
  const [tip, setTip] = useState();
  const [items, setItems] = useState([{ itemName:'', quantity: null, price: null }]);

  const handleItemChange = (index, key, value) => {
    const newItems = [...items];
    newItems[index][key] = value;
    setItems(newItems);
  };

  // const addItem = () => {
  //   setItems([...items, { itemName: '', quantity: null, price: null }]);
  // };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      await axios.post('http://localhost:5000/invoices', { clientName, items, tax, tip });
      fetchInvoices();
    } catch (error) {
      console.error('Error creating invoice', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>Customer Name</label>
      <input
        type="text"
        placeholder="Client Name"
        value={clientName}
        onChange={(e) => setClientName(e.target.value)}
        required
      />
      
      <label>Tax</label>
      <input
        type="number"
        placeholder="Tax"
        value={tax}
        onChange={(e) => setTax(parseFloat(e.target.value))}
        required
      />
      
      <label>Tip</label>
      <input
        type="number"
        placeholder="Tip"
        value={tip}
        onChange={(e) => setTip(parseFloat(e.target.value))}
        required
      />
      
      {items.map((item, index) => (
        <div key={index} className="invoice-item">
          <label>Item name</label>
          <input
            type="text"
            placeholder="Item Name"
            value={item.itemName}
            onChange={(e) => handleItemChange(index, 'itemName', e.target.value)}
            required
          />
          
          <label>Quantity</label>
          <input
            type="number"
            placeholder="Quantity"
            value={item.quantity}
            onChange={(e) => handleItemChange(index, 'quantity', parseInt(e.target.value))}
            required
          />
          
          <label>Price</label>
          <input
            type="number"
            placeholder="Price"
            value={item.price}
            onChange={(e) => handleItemChange(index, 'price', parseFloat(e.target.value))}
            required
          />
        </div>
      ))}
      
      {/* <button type="button" onClick={addItem}>Add Item</button> */}
      <button type="submit">Create Invoice</button>
    </form>
  );
};

export default InvoiceForm;
