import React, { useState } from 'react';
import axios from 'axios';

const InvoiceForm = ({ fetchInvoices,invoices }) => {
  const [clientName, setClientName] = useState('');
  const [tax, setTax] = useState('');
  const [tip, setTip] = useState('');
  const [items, setItems] = useState([{ itemName: '', quantity: '', price: '' }]);
  const [errors, setErrors] = useState({});

  const validate = () => {
    const newErrors = {};
    if (!clientName) newErrors.clientName = 'Client name is required';
    if (tax === '') newErrors.tax = 'Tax is required';
    if (tip === '') newErrors.tip = 'Tip is required';
    items.forEach((item, index) => {
      if (!item.itemName) newErrors[`itemName${index}`] = 'Item name is required';
      if (item.quantity === '') newErrors[`quantity${index}`] = 'Quantity is required';
      if (item.price === '') newErrors[`price${index}`] = 'Price is required';
    });
    return newErrors;
  };

  const handleItemChange = (index, key, value) => {
    const newItems = [...items];
    newItems[index][key] = value;
    setItems(newItems);
  };

  const addItem = () => {
    setItems([...items, { itemName: '', quantity: '', price: '' }]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const validationErrors = validate();
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }
    setErrors({});

    try {
      await axios.post('http://localhost:5000/invoices', { clientName, items, tax: parseFloat(tax), tip: parseFloat(tip) });
      fetchInvoices();
    } catch (error) {
      console.error('Error creating invoice', error);
    }
  };


  //invoice list
    const handlePrint = (invoice) => {
      const printContent = `
        <div>
          <h1>Invoice</h1>
          <p>Client: ${invoice.clientName}</p>
          <h2>Items</h2>
          <ul>
            ${invoice.items.map(item => `
              <li>${item.itemName} - ${item.quantity} x $${item.price}</li>
            `).join('')}
          </ul>
          <p>Tax: $${invoice.tax}</p>
          <p>Tip: $${invoice.tip}</p>
          <h3>Total: $${invoice.totalAmount}</h3>
        </div>
      `;
      const newWindow = window.open('', '_blank', 'width=800,height=600');
      newWindow.document.write(printContent);
      newWindow.document.close();
      newWindow.print();
    };
  
    const handleBill = (invoice) => {
      alert(`Billing ${invoice.clientName} for $${invoice.totalAmount}`);
    };
  
    const handleDelete = async (id) => {
      try {
        await axios.delete(`http://localhost:5000/invoices/${id}`);
        fetchInvoices();
      } catch (error) {
        console.error('Error deleting invoice', error);
      }
    };

  return (
    <div className='App'>
      <div className="form">
        <form onSubmit={handleSubmit}>
          <label>Customer Name</label>
          <input
            type="text"
            placeholder="Client Name"
            value={clientName}
            onChange={(e) => setClientName(e.target.value)}
          // required
          />
          {errors.clientName && <div className="error">{errors.clientName}</div>}

          <label>Tax</label>
          <input
            type="number"
            placeholder="Tax"
            value={tax}
            onChange={(e) => setTax(e.target.value)}
          // required
          />
          {errors.tax && <div className="error">{errors.tax}</div>}

          <label>Tip</label>
          <input
            type="number"
            placeholder="Tip"
            value={tip}
            onChange={(e) => setTip(e.target.value)}
          // required
          />
          {errors.tip && <div className="error">{errors.tip}</div>}

          {items.map((item, index) => (
            <div key={index} className="invoice-item">
              <label>Item name</label>
              <input
                type="text"
                placeholder="Item Name"
                value={item.itemName}
                onChange={(e) => handleItemChange(index, 'itemName', e.target.value)}
              // required
              />
              {errors[`itemName${index}`] && <div className="error">{errors[`itemName${index}`]}</div>}

              <label>Quantity</label>
              <input
                type="number"
                placeholder="Quantity"
                value={item.quantity}
                onChange={(e) => handleItemChange(index, 'quantity', e.target.value)}
              // required
              />
              {errors[`quantity${index}`] && <div className="error">{errors[`quantity${index}`]}</div>}

              <label>Price</label>
              <input
                type="number"
                placeholder="Price"
                value={item.price}
                onChange={(e) => handleItemChange(index, 'price', e.target.value)}
              //required
              />
              {errors[`price${index}`] && <div className="error">{errors[`price${index}`]}</div>}
            </div>
          ))}

          <button type="button" onClick={addItem}>Add Item</button>
          <button type="submit">Create Invoice</button>
        </form>
      </div>
    
      <div className='customer-invoices'>
        <h2>Customer and Invoice</h2>
        {invoices.map((invoice) => (
          <div key={invoice._id}  className="customer-section">
      
            <h3>Customer's Name:{invoice.clientName}</h3>
            <div className='invoice-actions'>
            <button className="printbtn" onClick={() => handlePrint(invoice)}>Print</button>
            <button className="totalbtn" onClick={() => handleBill(invoice)}>Bill</button>
            <button className="deletebtn" onClick={() => handleDelete(invoice._id)}>Delete</button>
          </div>
          </div>
        ))}
      </div>
    </div>
      );
};
      export default InvoiceForm;
